<script lang="ts">
	import { onMount } from "svelte";

	let theme: string;

	onMount(() => {
		theme = localStorage.getItem("theme") ?? "light";
		const windowWidth = window.innerWidth;
		const windowHeight = window.innerHeight;
		const iconsSection =
			document.getElementById("icons") || document.createElement("div");
		const techIcons = Array.from(iconsSection.getElementsByTagName("svg"));

		const animateIcons = () => {
			const availableIcons = techIcons.filter(
				(icon) => !icon.classList.contains("animate-tech-icon"),
			);
			const icon =
				availableIcons[Math.floor(Math.random() * availableIcons.length)];

			if (!icon) return;

			icon.addEventListener(
				"animationend",
				() => {
					icon.classList.remove("animate-tech-icon");
				},
				{ once: true },
			);

			icon.setAttribute(
				"style",
				`top: ${Math.floor(Math.random() * windowHeight - 100)}px;
                    left: ${Math.floor(Math.random() * windowWidth - 100)}px;`,
			);
			icon.classList.add("animate-tech-icon");
		};
		animateIcons();
		setInterval(animateIcons, 1000);
	});
</script>

